targets.query:
from(bucket:"monitor")
  |> range(start: -5m)
  |> filter(fn: (r) => r._measurement == "http_response" and r._field == "result_type" and r.server == "https://www.bennerleyviaduct.org.uk")
  |> drop(columns:["_start","_stop","_field","_measurement","host","method","server"])
  |> map(fn: (r) =>({
       _time: r._time,
       _value: if r._value == "success" and r.status_code == "200" then "Ok" else if r._value == "success" then r.status_code else r._value
      }))

